contract = web3.eth.contract(address=contract_address, abi=abi)

# Fonction pour gérer les événements
def handle_event(event):
    print(f"Nouvel événement détecté : {event}")


# Récupération du dernier bloc traité

last_block_processed = 0  # Remplacez par le dernier bloc que vous avez traité


# Boucle infinie pour vérifier les nouveaux événements
while True:
    # Récupération du dernier bloc
    latest_block = web3.eth.block_number

    # Récupération des nouveaux événements depuis le dernier bloc traité
    events = contract.events.all-events.get_new_entries(from_block=last_block_processed+1, to_block=latest_block)

    # Traitement des événements
    for event in events:
        handle_event(event)


    # Mise à jour du dernier bloc traité
    last_block_processed = latest_block

